import React from "react";
import Mensagem from "../views/mensagem";


function Visivel(props){
    console.log(props.opcao);
        if (props.opcao === 'sms') {
            return (
                <div >
                    <button onClick={Mensagem.enviarSms} className="btn btn-info" > enviar sms</button>
                    <p >{Mensagem.mensagemResponse}</p>
                    <button onClick={Mensagem.enviarSmsTodos} className="btn btn-primary" > enviar sms para todos</button>
                </div>
            )
        }

        return (
            <div >
                <button onClick={Mensagem.enviarWhatsApp} className="btn btn-info" > enviar whatsApp</button>
                <p >{Mensagem.mensagemResponse}</p>
                <button onClick={Mensagem.enviarSmsTodos} className="btn btn-primary" > enviar whatsApp para todos</button>
            </div>
        )
}export default Visivel;
