import React from "react";
import Card from "../components/card";
import FormGroup from "../components/form-group";
import UsuarioService from "../app/service/usuarioService";
import { mensagemSucesso, mensagemError } from "../components/toastr";
import { withRouter } from 'react-router-dom';
import currencyFormatter from 'currency-formatter';
import LocalStorageService from "../app/service/localStorageService";

class CadastroUsuario extends React.Component {

    state = {
        nome: '',
        email: '',
        senha: '',
        senharepita: '',
        itens: []
    }

    constructor() {
        super();
        this.service = new UsuarioService();
    }

    componentDidMount() {
        const params = this.props.match.params;
        const carrinho = LocalStorageService.obterItem('_carrinho');
        console.log(carrinho)
        const array = []
        for (let i = 0; i < carrinho.length; i++) {
            const itemCompra = {
                id: null,
                produto: carrinho[i],
                quantidade: 0,
                compra: null
            }
            this.setState({ itens: [...this.state.itens, itemCompra] });
        }
    }

    remove = (index) => {
       
        console.log(this.state.itens);
    }

    add = (index) => {
        for(var i; i <= index; i++){
            if(i == index){
                this.state.itens[i].quantidade = i;
            }
        }
        this.setState(itens[index].quantidade++);

        console.log(this.state.itens);
    }

    render() {
        return (
            <div className="">
                <Card title="Itens">
                    {
                        this.state.itens.map((e, index) => {
                            return (
                                <div key={index} className="container row col-sm-12">
                                    <div className="col-sm-1">
                                        <img width={50} height={50} src={`data:image/jpeg;base64,${e.produto.foto}`} />
                                    </div>
                                    <div className="col-sm-6" style={{ justifyContent: "center", alignItems: "center", flexDirection: "row", height: 50 }}>
                                        <h6 >{e.produto.nome + " - " + currencyFormatter.format(e.produto.preco, { locale: 'pt-BR' })}</h6>
                                    </div>
                                    <button className="btn btn-dark" onClick={this.remove(index)} style={{ width: 30, height: 30 }}><i className="pi pi-minus" style={{ fontSize: '0.5rem' }}></i></button>
                                    <input className="form-control" value={e.quantidade} style={{ width: 45, height: 30 }} maxLength={2} />
                                    <button className="btn btn-dark" style={{ width: 30, height: 30 }}><i className="pi pi-plus" style={{ fontSize: '0.5rem' }}></i></button>
                                    <hr></hr>
                                </div>
                            )
                        })
                    }
                </Card>

            </div>
        );
    }
}
export default withRouter(CadastroUsuario);
