import React from "react";
import currencyFormatter from 'currency-formatter';


export default props => {

    const rows = props.produtos.map(produto => {
        return (
            <tr key={produto.id}>
                <td>{produto.descricao}</td>
                <td>{currencyFormatter.format(produto.valor, { locale: 'pt-BR' })}</td>
                <td>{produto.tipo}</td>
                <td>
                    <button className="btn btn-primary" type="button" onClick={e => props.editar(produto.id)}>editar</button>
                    <button className="btn btn-danger" type="button" onClick={e => props.delete(produto)}>deletar</button>
                </td>
            </tr>
        )
    })

    return (
        <table className="table table-hover">
            <thead>
                <tr>
                    <th scope="col">Nome</th>
                    <th scope="col">Pre√ßo</th>
                    <th scope="col">estoque</th>
                    <th scope="col">Acoes</th>
                </tr>
            </thead>
            <tbody>
                {rows}
            </tbody>
        </table>
    )
}
