import React from "react";
import axios from "axios";

class Mensagem extends React.Component {
    state = {
        sms: '',
        usuario: '',
        mensagemResponse: '',
        whatsApp: '',
        opcao: ''
    }

    componentDidMount() {
        const usuario = localStorage.getItem('_usuario');
        this.setState({ usuario: JSON.parse(usuario) });
    }

    enviarSms = () => {
        axios.post('http://localhost:8080/api/usuario/enviar-sms-usuario', {
            usuario: this.state.usuario,
            mensagem: this.state.sms
        }).then(response => {
            console.log(response)
            this.setState({ mensagemResponse: 'Mensagem enviada com sucesso' })
        }).catch(err => {
            console.log(err)
            this.setState({ mensagemResponse: err.response.data })
        })
    }

    enviarSmsTodos = () => {
        axios.post('http://localhost:8080/api/usuario/enviar-sms-todos', {
            usuario: '',
            mensagem: this.state.sms
        }).then(response => {
            console.log(response)
            this.setState({ mensagemResponse: 'Mensagem enviada com sucesso' })
        }).catch(err => {
            console.log(err)
            this.setState({ mensagemResponse: err.response.data })
        })
    }

    enviarWhatsApp = () => {
        axios.post('http://localhost:8080/api/usuario/enviar-wpp-usuario', {
            usuario: this.state.usuario,
            mensagem: this.state.whatsApp
        }).then(response => {
            console.log(response)
            this.setState({ mensagemResponse: response.data })
        }).catch(err => {
            console.log(err)
            this.setState({ mensagemResponse: err.response.data })
        })
    }

    isVisivel = ({ opcao }) => {
        if (opcao == 'sms') {
            return (
                <div >
                    <p>{this.state.usuario.nome}</p>
                    <p>{this.state.usuario.telefone}</p>
                    <p>{this.state.usuario.email}</p>
                    <div>
                        <textarea value={this.state.sms} style={{ width: '500px' }}
                            onChange={e => this.setState({ sms: e.target.value })} />
                    </div>
                    <button onClick={this.enviarSms} className="btn btn-info" > enviar sms</button>
                    <p >{this.state.mensagemResponse}</p>
                    <button onClick={this.enviarSmsTodos} className="btn btn-primary" > enviar sms para todos</button>
                </div>
            )
        }

        return (
            <div>
                <p>{this.state.usuario.nome}</p>
                <p>{this.state.usuario.telefone}</p>
                <p>{this.state.usuario.email}</p>
                <div>
                    <textarea value={this.state.whatsApp} style={{ width: '500px' }}
                        onChange={e => this.setState({ whatsApp: e.target.value })} />
                </div>
                <button onClick={this.enviarWhatsApp} className="btn btn-info" > enviar whatsApp</button>
                <p >{this.state.mensagemResponse}</p>
                <button onClick={this.enviarSmsTodos} className="btn btn-primary" > enviar whatsApp para todos</button>
            </div>
        )

    }

    render() {
        return (
            <div isVisivel={this.state.opcao}>


            </div>
        )
    }
} export default Mensagem;
