import React from "react";
import Card from "../components/card";
import FormGroup from "../components/form-group";
import UsuarioService from "../app/service/usuarioService";
import { mensagemSucesso, mensagemError } from "../components/toastr";
import { withRouter } from 'react-router-dom';
import currencyFormatter from 'currency-formatter';
import LocalStorageService from "../app/service/localStorageService";
import { InputNumber } from 'primereact/inputnumber';

class CadastroUsuario extends React.Component {

    state = {
        nome: '',
        email: '',
        senha: '',
        senharepita: '',
        itens: [],
        valor: 0
    }

    constructor() {
        super();
        this.service = new UsuarioService();
    }

    componentDidMount() {
        const params = this.props.match.params;
        const carrinho = LocalStorageService.obterItem('_carrinho');
        console.log(carrinho)
        this.setState({ itens: carrinho })
    }

    render() {
        return (
            <div className="">
                <Card title="Itens">
                    {
                        this.state.itens.map((e, index) => {
                            return (
                                <div key={index} className="container row col-sm-12">
                                    <div className="col-sm-1">
                                        <img width={50} height={50} src={`data:image/jpeg;base64,${e.foto}`} />
                                    </div>
                                    <div className="col-sm-6" style={{ justifyContent: "center", alignItems: "center", flexDirection: "row", height: 50 }}>
                                        <h6 >{e.nome + " - " + currencyFormatter.format(e.preco, { locale: 'pt-BR' })}</h6>
                                    </div>
                                    <InputNumber value={this.state.valor} onValueChange={(e) => this.setValue({valor: e.valor})} showButtons buttonLayout="vertical" style={{ width: '4rem' }}
                                        decrementButtonClassName="p-button-secondary" incrementButtonClassName="p-button-secondary" incrementButtonIcon="pi pi-plus" decrementButtonIcon="pi pi-minus" />

                                    <hr></hr>
                                </div>
                            )
                        })
                    }
                </Card>

            </div>
        );
    }
}
export default withRouter(CadastroUsuario);
