import React from "react";
import axios from "axios";
import Visivel from "../components/visivel";

class Mensagem extends React.Component {
    state = {
        sms: '',
        usuario: '',
        mensagemResponse: '',
        whatsApp: '',
        opcao: ''
    }

    componentDidMount() {
        const usuario = localStorage.getItem('_usuario');
        this.setState({ usuario: JSON.parse(usuario) });
    }

    enviarSms = () => {
        axios.post('http://localhost:8080/api/usuario/enviar-sms-usuario', {
            usuario: this.state.usuario,
            mensagem: this.state.sms
        }).then(response => {
            console.log(response)
            this.setState({ mensagemResponse: 'Mensagem enviada com sucesso' })
        }).catch(err => {
            console.log(err)
            this.setState({ mensagemResponse: err.response.data })
        })
    }

    enviarSmsTodos = () => {
        axios.post('http://localhost:8080/api/usuario/enviar-sms-todos', {
            usuario: '',
            mensagem: this.state.sms
        }).then(response => {
            console.log(response)
            this.setState({ mensagemResponse: 'Mensagem enviada com sucesso' })
        }).catch(err => {
            console.log(err)
            this.setState({ mensagemResponse: err.response.data })
        })
    }

    enviarWhatsApp = () => {
        axios.post('http://localhost:8080/api/usuario/enviar-wpp-usuario', {
            usuario: this.state.usuario,
            mensagem: this.state.sms
        }).then(response => {
            console.log(response)
            this.setState({ mensagemResponse: response.data })
        }).catch(err => {
            console.log(err)
            this.setState({ mensagemResponse: err.response.data })
        })
    }


    render() {
        return (
            <div>
                <select value={this.state.value} onChange={e => this.setState({ opcao: e.target.value })} >
                    <option value="wpp" >..  Whats App  ..</option>
                    <option value="sms" >SMS</option>
                </select>
               
                <h6 style={{marginTop: '50px'}}>{this.state.usuario.nome}</h6>
                <h6>{this.state.usuario.telefone}</h6>
                <h6>{this.state.usuario.email}</h6>
                <div>
                    <textarea value={this.state.sms} style={{ width: '500px' }}
                        onChange={e => this.setState({ sms: e.target.value })} />
                </div>
                <Visivel opcao={this.state.opcao}
                    enviarWhatsApp={this.enviarWhatsApp}
                    enviarSms={this.enviarSms}
                    enviarSmsTodos={this.enviarSmsTodos}
                    mensagemResponseSms={this.state.mensagemResponse}
                    mensagemResponseWpp={this.state.mensagemResponse} />
            </div>
        )
    }
} export default Mensagem;
